mapper.select:
select id as id, user_code as userCode, demand as demand, demand_type as demandType, plan_start_time as planStartTime, plan_end_time as planEndTime, plan_test_time as planTestTime, plan_online_time as planOnlineTime, start_time as startTime, end_time as endTime, test_time as testTime, remark as remark, status as status, logic_state as logicState, create_user as createUser, create_time as createTime, modify_user as modifyUser, modify_time as modifyTime from demand where logic_state = 'Y'
and user_code = #{userCode}
and demand like CONCAT('%', #{demand}, '%')
and demand_type = #{demandType}
and start_time >= #{startTime}
and start_time <= #{endTime}
and status = #{status}
order by create_time desc

mapper.delete:
update demand
set status = #{status}
        where id in

    #{id}
