mapper.select:
select id as id, name as name, dept as dept, area as area, user_code as userCode, remark as remark, logic_state as logicState, create_user as createUser, create_time as createTime, modify_user as modifyUser, modify_time as modifyTime from sys_user_detail where logic_state = 'Y'
and name like CONCAT('%', #{name}, '%')
and dept = #{dept}
and area = #{area}
ORDER BY CASE when user_code = #{userCode} THEN 0 ELSE 1 END, id DESC

select user_code
from sys_user_detail where id in

    #{id}
